DataSet ds = new DataSet();
ds.ReadXml(new StringReader(xml));

if (TableName == "" && (int)TableIndex < ds.Tables.Count && (int)TableIndex >=0){
	hasDataTable = true;
	XMLDataTable = ds.Tables[(int)TableIndex];
} 
else if (ds.Tables[TableName] != null) {
	hasDataTable = true;
	XMLDataTable = ds.Tables[TableName];
} 
else {
	hasDataTable = false;
	XMLDataTable = new DataTable();
}

DataSet ds = new DataSet();
ds.ReadXml(new StringReader(xml));

XMLDataSet = new DataTable();
XMLDataSet.Columns.Add("ID", typeof(decimal));
XMLDataSet.Columns.Add("Name", typeof(string));
if (withDataTables) 
	XMLDataSet.Columns.Add("Table", typeof(DataTable));

foreach(DataTable table in ds.Tables)
{
	DataRow dr = XMLDataSet.NewRow();
	dr["ID"] = ds.Tables.IndexOf(table);
	dr["Name"] = table.TableName;
	if (withDataTables) 
		dr["Table"] = table;
	XMLDataSet.Rows.Add(dr);
}

